def movingaverage(data,days=2):
    ma=[]
    for i in range(days-1,len(data)):
        sum=0
        for j in range(days):
            sum=sum+data[i-j][1]
        avg=round(sum/days,1)

        ma.append((data[i][0],avg))
    return ma
